!function () {
    "use strict";

    class e {
        static initValidation() {
            let alert = Swal.mixin({
                buttonsStyling: !1,
                target: "#page-container",
                customClass: {
                    confirmButton: "btn btn-primary m-1",
                    cancelButton: "btn btn-danger m-1",
                    input: "form-control"
                },
                showConfirmButton: false,
                showDenyButton: false,
                showCancelButton: false,
                showCloseButton: true,
                timer: 5000,
                timerProgressBar: true,
            });

            One.helpers("jq-validation"), jQuery(".js-validation-signin").validate({
                submitHandler: function (form, e) {
                    e.preventDefault();
                    $.ajaxSetup({
                        headers: {
                            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                        }
                    });

                    $.ajax({
                        type: $(form).attr('method'),
                        url: $(form).attr('action'),
                        data: $(form).serialize(),
                        dataType: 'json',
                        beforeSend: function () {
                            $(form).find('[type=submit]').attr('disabled', 'disabled');
                            $(form).find('[type=submit] i').removeClass('fa-sign-in-alt');
                            $(form).find('[type=submit] i').addClass('fa-spinner fa-pulse');
                        },
                        success: function (response) {
                            window.location.href = response.redirect;
                        },
                        error: function (response) {
                            alert.fire({
                                text: response.responseJSON.message,
                                icon: 'error'
                            });
                        }
                    }).always(function () {
                        setTimeout(() => {
                            $(form).find('[type=submit]').removeAttr('disabled');
                            $(form).find('[type=submit] i').addClass('fa-sign-in-alt');
                            $(form).find('[type=submit] i').removeClass('fa-spinner fa-pulse');
                        }, 500)
                    });
                }
            });

            $('.dropify').dropify({
                messages: {
                    'default': 'Bir fotoğraf seçin',
                    'replace': 'Değiştirmek için tıklayın',
                    'remove':  'Kaldır',
                    'error':   'Hata! Yanlış bir şey oldu.'
                },
                error: {
                    'fileSize': 'Dosya boyutu çok büyük ({{ value }} maks).',
                    'minWidth': 'Resim genişliği çok küçük ({{ value }}}px min).',
                    'maxWidth': 'Resim genişliği çok büyük ({{ value }}}px max).',
                    'minHeight': 'Resim yüksekliği çok küçük ({{ value }}}px min).',
                    'maxHeight': 'Resim yüksekliği çok büyük ({{ value }}px max).',
                    'imageFormat': 'Resim formatına izin verilmiyor (yalnızca {{ value }})',
                    'fileExtension': 'Dosyaya izin verilmiyor (yalnızca {{ value }}).'
                }
            });
        }

        static init() {
            One.helpersOnLoad(['js-flatpickr']);
            this.initValidation()
        }
    }

    One.onLoad((() => e.init()))
}();
